(function() {
  var Employee, HeaderMenu, setHeaderMenu;

  Employee = Window.Employee || {};

  $(document).ready(function() {
    var quotesCarousel, skillChart;
    $('[data-toggle="tooltip"]').tooltip();
    quotesCarousel = $('#slick-quotes');
    skillChart = $('.skill-chart');
    setHeaderMenu.setHeader();
    quotesCarousel.slick({
      autoplay: true,
      arrows: false
    });
    $('#slick-q-prev').on('click', function() {
      return quotesCarousel.slick('slickPrev');
    });
    $('#slick-q-next').on('click', function() {
      return quotesCarousel.slick('slickNext');
    });
    if (skillChart.length > 0) {
      skillChart.easyPieChart({
        lineWidth: 10,
        size: 180
      });
    }
    return false;
  });

  $(window).resize(function() {
    setHeaderMenu.setHeader();
    return false;
  });

  HeaderMenu = (function() {
    function HeaderMenu() {
      this.fixedHeader = $('#employee-header');
      this.heightOfWindow = $(window).height();
    }

    HeaderMenu.prototype.setHeader = function() {
      this.fixedHeader.affix({
        offset: {
          top: this.heightOfWindow,
          bottom: function(e) {}
        }
      });
      return false;
    };

    return HeaderMenu;

  })();

  setHeaderMenu = new HeaderMenu();

}).call(this);
